import React from 'react';

const NewsList = ({ articles }) => {
  const placeholderImage = "https://via.placeholder.com/600x300?text=No+Image";

  if (!articles.length) {
    return <p>No news articles found.</p>;
  }

  return (
    <div>
      {articles.map((article, idx) => (
        <div key={idx} style={{ marginBottom: '20px', borderBottom: '1px solid #ccc', paddingBottom: '10px' }}>
          {(article.urlToImage || placeholderImage) && (
            <img 
              src={article.urlToImage || placeholderImage} 
              alt={article.title} 
              style={{ width: '100%', maxHeight: '300px', objectFit: 'cover' }} 
              onError={(e) => e.target.src = placeholderImage} 
              loading="lazy"
            />
          )}
          <h3>
            <a href={article.url} target="_blank" rel="noopener noreferrer">{article.title}</a>
          </h3>
          <p>{article.description}</p>
          <small>{article.source.name} | {new Date(article.publishedAt).toLocaleString()}</small>
        </div>
      ))}
    </div>
  );
};

export default NewsList;
